package com.javaArrays;

public class Example4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// one line array declaration and initialization
		int studentMarks[] = { 10, 20, 30, 40, 50 };

		System.out.println(studentMarks.length);// 5
		System.out.println(studentMarks[0]);// 10
		System.out.println(studentMarks[studentMarks.length - 1]);// 50

	}

}
